jQuery(document).ready(function() {
    let MRPP = window.MRPP;
    let $Ethers = window.ethers;
    let $Handlebars = window.Handlebars;
    console.log("MRPP Loader:: Document ready executing...");
    if (typeof MRPP === "undefined") {
        console.log("MRPP Loader:: Error loading MRPP.");
    } else {
        if (typeof $Ethers === "undefined") {
            console.log("MRPP Loader:: Error loading ethers.");
        } else {
            console.log($Ethers);
            if (typeof $Handlebars === "undefined") {
                console.log("MRPP Loader:: Error loading Handlebars.");
            } else {
                console.log("MRPP Loader:: Dependencies confirmed.");
                MRPP.$ = window.jQuery;
                MRPP.$Eths = $Ethers;
                MRPP.$Hb = $Handlebars;
                MRPP.State.Initialized = true;
                console.log(MRPP);
            }
        }
    }
    let prod = false;
    if (prod) {
        MRPP.GlobalConfig.SelectedContract = MRPP.GlobalConfig.MainnetContract;
    } else {
        MRPP.GlobalConfig.SelectedContract = MRPP.GlobalConfig.RinkebyContract;
    }
    MRPP.RunProgram({
        autoConnect: true,
        enableAlerts: true,
        enableMinter: true,
        enableBurning: false
    });
});